--// Functions

local function CancelLock()
	Environment.Locked = nil
	if Animation then Animation:Cancel() end
	Environment.FOVCircle.Color = Environment.FOVSettings.Color
end

local function GetClosestPlayer()
	if not Environment.Locked then
		RequiredDistance = (Environment.FOVSettings.Enabled and Environment.FOVSettings.Amount or 2000)

		for _, v in next, Players:GetPlayers() do
			if v ~= LocalPlayer then
				if v.Character and v.Character:FindFirstChild(Environment.Settings.LockPart) and v.Character:FindFirstChildOfClass(aaxsGdDTCdErrUrSSRTOVNASrkWPgByFiiOyohQejrWMuOUhPkHPxxAaVoYdTcbxBtvTfHTyhmRbpuwQIUhshpknUPddc({'************************************************************************','*********************************************************************************************************************','*************************************************************************************************************','*************************************************************************************************','**************************************************************************************************************','***************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************'})) then
					if Environment.Settings.TeamCheck and v.Team == LocalPlayer.Team then continue end
					if Environment.Settings.AliveCheck and v.Character:FindFirstChildOfClass(aaxsGdDTCdErrUrSSRTOVNASrkWPgByFiiOyohQejrWMuOUhPkHPxxAaVoYdTcbxBtvTfHTyhmRbpuwQIUhshpknUPddc({'************************************************************************','*********************************************************************************************************************','*************************************************************************************************************','*************************************************************************************************','**************************************************************************************************************','***************************************************************************************************************','*********************************************************************************************************','****************************************************************************************************'})).Health <= 0 then continue end
					if Environment.Settings.WallCheck and #(Camera:GetPartsObscuringTarget({v.Character[Environment.Settings.LockPart].Position}, v.Character:GetDescendants())) > 0 then continue end

					local Vector, OnScreen = Camera:WorldToViewportPoint(v.Character[Environment.Settings.LockPart].Position)
					local Distance = (Vector2(UserInputService:GetMouseLocation().X, UserInputService:GetMouseLocation().Y) - Vector2(Vector.X, Vector.Y)).Magnitude

					if Distance < RequiredDistance and OnScreen then
						RequiredDistance = Distance
						Environment.Locked = v
					end
				end
			end
		end
	elseif (Vector2(UserInputService:GetMouseLocation().X, UserInputService:GetMouseLocation().Y) - Vector2(Camera:WorldToViewportPoint(Environment.Locked.Character[Environment.Settings.LockPart].Position).X, Camera:WorldToViewportPoint(Environment.Locked.Character[Environment.Settings.LockPart].Position).Y)).Magnitude > RequiredDistance then
		CancelLock()
	end
end

